import React, { useState, useEffect, useMemo } from 'react';
import FeaturedInfo from '../../components/feature-info/FeaturedInfo';
import Chart from '../../components/chart/Chart';
import { userData } from '../../dummyData.js';
import WidgetSm from '../../components/widget-sm/WidgetSm';
import WidgetLg from '../../components/widget-lg/WidgetLg';
import axios from 'axios';

import './home.scss';


export default function Home() {
    const MONTHS = useMemo(() => 
        [
            "Jan", 
            "Feb", 
            "Mar", 
            "Apr", 
            "May", 
            "Jun", 
            "Jul",
            "Aug", 
            "Sep", 
            "Oct", 
            "Nov", 
            "Dec"
        ], []
    );

    const [userStats, setUserStats] = useState();

    useEffect(() => {
        const getStats = async () => {
            try {
                const res = await axios.get('/users/stats', {
                    headers: {
                        token: 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxMWE0OWNjZDZiNWJiOTA0Zjg0ZmQ0MCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYyOTEzMjA5MiwiZXhwIjoxNjI5NTY0MDkyfQ.2iuQWwT2D0E23AONA-M5zgXjBzvCYD5M3IBQg_NocrA'
                    }
                });
                res.data.map((item) => 
                    setUserStats((prev) => [
                        ...prev, 
                        { name: MONTHS[item._id - 1], 'New User': item.total },
                    ])
                );
            } catch (err) {
                console.log(err);
            }
        };
        getStats();
    }, [MONTHS]);

    console.log(userStats);
    return (
        <div className='home'>
            <FeaturedInfo />
            <Chart data={userData} title="User Analytics" grid dataKey='Active User' />
            <div className='homeWidgets'>
                <WidgetSm />
                <WidgetLg />
            </div>
        </div>
    );
}
