import React, { useState, useEffect } from 'react';
import Navbar from '../../components/navbar/Navbar';
import Featured from '../../components/featured/Featured';
import List from '../../components/list/List';
import axios from 'axios';

import './home.scss';


const Home = ({ type }) => {
    const [lists, setLists] = useState([]);
    const [genre, setGenre] = useState(null);

    useEffect(() => {
        const getRandomLists = async () => {
            try {
                const res = await axios.get(
                    `lists${type ? '?type=' + type: ''}${genre ? '&genre=' + genre: ''}`, {
                    headers :{
                        token: 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxMWE0OWNjZDZiNWJiOTA0Zjg0ZmQ0MCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYyOTEzMjA5MiwiZXhwIjoxNjI5NTY0MDkyfQ.2iuQWwT2D0E23AONA-M5zgXjBzvCYD5M3IBQg_NocrA'
                    }
                });
                setLists(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        getRandomLists();
    }, [type, genre]);

    return (
        <div className='home'>
            <Navbar />
            <Featured type={type}/>
            <List />
            <List />
            <List />
        </div>
    );
};

export default Home;
