import React, { useState, useEffect } from 'react';
import { PlayArrow, InfoOutlined } from '@material-ui/icons';
import './featured.scss';
import axios from 'axios';

export default function Featured({ type }) {
    const [content, setContent] = useState({});

    useEffect(() => {
        const getRandomContent = async () => {
            try {
                const res = await axios(`/movies/random?type=${type}`, {
                    headers :{
                        token: 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxMWE0OWNjZDZiNWJiOTA0Zjg0ZmQ0MCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzMDkyNTY4MiwiZXhwIjoxNjMxNTMwNDgyfQ.YJjUqFTwLuitH-0xY4ANXmrCX2_u1i5T2LD8Z03FIjg'
                    },
                });
                setContent(res.data[0]);

            } catch (err) {
                console.log(err);
            }
        }
        getRandomContent();
    }, [type]);
    console.log(content)
    return (
        <div className='featured'>
            {type && (
                <div className='category'>
                    <span>{type === 'movies' ? 'Movies' : 'Series'}</span>
                    <select name='genre' id='genre'>
                        <option>Genre</option>
                        <option value='adventure'>Adventure</option>
                        <option value='comedy'>Comedy</option>
                        <option value='crime'>Crime</option>
                        <option value='fantasy'>Fantasy</option>
                        <option value='historical'>Historical</option>
                        <option value='horror'>Horror</option>
                        <option value='romance'>Romance</option>
                        <option value='sci-fi'>Sci-fi</option>
                        <option value='thriller'>Thriller</option>
                        <option value='western'>Western</option>
                        <option value='animation'>Animation</option>
                        <option value='drama'>Drama</option>
                        <option value='documentary'>Documentary</option>
                    </select>
                </div>
            )}
            <img 
                src={content.img}
                 alt='' 
            />
            <div className='info'>
                <img 
                    src={content.imgTitle}
                     alt='' 
                />
                <span className='desc'>{content.desc}</span>
                <div className='buttons'>
                    <button className='play'>
                        <PlayArrow />
                        <span>Play</span>                    
                    </button>
                    <button className='more'>
                        <InfoOutlined />
                        <span>Info</span>
                    </button>
                </div>
            </div>
        </div>
    )
}
